
public class MultiplicationTable {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
         for(int i=1;i<=10;i++) {
        	 System.out.println("13 * "+i+"\t=\t"+(13*i));
        	 
         }
         System.out.println("\n");
         
         for(int j=1;j<=10;j++) {
        	 System.out.println("45 * "+j+"\t=\t"+(45*j));
        	 
         }
         System.out.println("\n");
         
         for(int k=1;k<=10;k++) {
        	 System.out.println("79 * "+k+"\t=\t"+(79*k));
        	
         }
         System.out.println("\n");
	}

}
